import mongoose, { Schema } from 'mongoose';
const TaskSchema = new Schema({
  content: String,
  createdAt: {
    type: Date,
    default: Date.now
  },
  images: [String],
  tips: [String],
  tags: [String],
  title: String,
  _user: {
    type: Schema.Types.ObjectId,
    ref: 'User'
  }
});
export default mongoose.models.Task || mongoose.model('Task', TaskSchema);
